SELECT COUNT(*) FROM ((SELECT DISTINCT c_last_name, c_first_name, d_date FROM postgres.public.store_sales, postgres.public.date_dim, mariadb.db1.customer WHERE store_sales.ss_sold_date_sk = date_dim.d_date_sk AND store_sales.ss_customer_sk = customer.c_customer_sk AND d_month_seq BETWEEN 1212 AND 1212 + 11) EXCEPT (SELECT DISTINCT c_last_name, c_first_name, d_date FROM postgres.public.catalog_sales, mysql.db1.date_dim, postgres.public.customer WHERE catalog_sales.cs_sold_date_sk = date_dim.d_date_sk AND catalog_sales.cs_bill_customer_sk = customer.c_customer_sk AND d_month_seq BETWEEN 1212 AND 1212 + 11) EXCEPT (SELECT DISTINCT c_last_name, c_first_name, d_date FROM postgres.public.web_sales, mysql.db1.date_dim, mariadb.db1.customer WHERE web_sales.ws_sold_date_sk = date_dim.d_date_sk AND web_sales.ws_bill_customer_sk = customer.c_customer_sk AND d_month_seq BETWEEN 1212 AND 1212 + 11)) AS cool_cust