SELECT CASE WHEN (SELECT COUNT(*) FROM postgres.public.store_sales WHERE ss_quantity BETWEEN 1 AND 20) > 25437 THEN (SELECT AVG(ss_ext_discount_amt) FROM mysql.db1.store_sales WHERE ss_quantity BETWEEN 1 AND 20) ELSE (SELECT AVG(ss_net_profit) FROM mysql.db1.store_sales WHERE ss_quantity BETWEEN 1 AND 20) END AS bucket1, CASE WHEN (SELECT COUNT(*) FROM mysql.db1.store_sales WHERE ss_quantity BETWEEN 21 AND 40) > 22746 THEN (SELECT AVG(ss_ext_discount_amt) FROM mysql.db1.store_sales WHERE ss_quantity BETWEEN 21 AND 40) ELSE (SELECT AVG(ss_net_profit) FROM postgres.public.store_sales WHERE ss_quantity BETWEEN 21 AND 40) END AS bucket2, CASE WHEN (SELECT COUNT(*) FROM postgres.public.store_sales WHERE ss_quantity BETWEEN 41 AND 60) > 9387 THEN (SELECT AVG(ss_ext_discount_amt) FROM mysql.db1.store_sales WHERE ss_quantity BETWEEN 41 AND 60) ELSE (SELECT AVG(ss_net_profit) FROM mariadb.db1.store_sales WHERE ss_quantity BETWEEN 41 AND 60) END AS bucket3, CASE WHEN (SELECT COUNT(*) FROM mysql.db1.store_sales WHERE ss_quantity BETWEEN 61 AND 80) > 10098 THEN (SELECT AVG(ss_ext_discount_amt) FROM mysql.db1.store_sales WHERE ss_quantity BETWEEN 61 AND 80) ELSE (SELECT AVG(ss_net_profit) FROM mariadb.db1.store_sales WHERE ss_quantity BETWEEN 61 AND 80) END AS bucket4, CASE WHEN (SELECT COUNT(*) FROM postgres.public.store_sales WHERE ss_quantity BETWEEN 81 AND 100) > 18213 THEN (SELECT AVG(ss_ext_discount_amt) FROM postgres.public.store_sales WHERE ss_quantity BETWEEN 81 AND 100) ELSE (SELECT AVG(ss_net_profit) FROM mysql.db1.store_sales WHERE ss_quantity BETWEEN 81 AND 100) END AS bucket5 FROM mysql.db1.reason WHERE r_reason_sk = 1