SELECT i_brand_id AS brand_id, i_brand AS brand, i_manufact_id, i_manufact, SUM(ss_ext_sales_price) AS ext_price FROM mariadb.db1.date_dim, mariadb.db1.store_sales, mariadb.db1.item, postgres.public.customer, mariadb.db1.customer_address, mariadb.db1.store WHERE d_date_sk = ss_sold_date_sk AND ss_item_sk = i_item_sk AND i_manager_id = 7 AND d_moy = 11 AND d_year = 1999 AND ss_customer_sk = c_customer_sk AND c_current_addr_sk = ca_address_sk AND SUBSTRING(ca_zip, 1, 5) <> SUBSTRING(s_zip, 1, 5) AND ss_store_sk = s_store_sk GROUP BY i_brand, i_brand_id, i_manufact_id, i_manufact ORDER BY ext_price DESC, i_brand NULLS FIRST, i_brand_id NULLS FIRST, i_manufact_id NULLS FIRST, i_manufact NULLS FIRST LIMIT 100