SELECT * FROM (SELECT w_warehouse_name, i_item_id, SUM(CASE WHEN (CAST(d_date AS DATE) < CAST('1998-04-08' AS DATE)) THEN inv_quantity_on_hand ELSE 0 END) AS inv_before, SUM(CASE WHEN (CAST(d_date AS DATE) >= CAST('1998-04-08' AS DATE)) THEN inv_quantity_on_hand ELSE 0 END) AS inv_after FROM mariadb.db1.inventory, postgres.public.warehouse, postgres.public.item, mysql.db1.date_dim WHERE i_current_price BETWEEN 0.99 AND 1.49 AND i_item_sk = inv_item_sk AND inv_warehouse_sk = w_warehouse_sk AND inv_date_sk = d_date_sk AND d_date BETWEEN (CAST('1998-04-08' AS DATE) - 30 AS days) AND (CAST('1998-04-08' AS DATE) + 30 AS days) GROUP BY w_warehouse_name, i_item_id) AS x WHERE (CASE WHEN inv_before > 0 THEN CAST(inv_after AS DOUBLE) / inv_before ELSE NULL END) BETWEEN CAST(2.0 AS DOUBLE) / 3.0 AND CAST(3.0 AS DOUBLE) / 2.0 ORDER BY w_warehouse_name NULLS FIRST, i_item_id NULLS FIRST LIMIT 100