WITH ss AS (SELECT i_manufact_id, SUM(ss_ext_sales_price) AS total_sales FROM mysql.db1.store_sales, mariadb.db1.date_dim, mariadb.db1.customer_address, mariadb.db1.item WHERE i_manufact_id IN (SELECT i_manufact_id FROM postgres.public.item WHERE i_category IN ('Books')) AND ss_item_sk = i_item_sk AND ss_sold_date_sk = d_date_sk AND d_year = 1999 AND d_moy = 3 AND ss_addr_sk = ca_address_sk AND ca_gmt_offset = -5 GROUP BY i_manufact_id), cs AS (SELECT i_manufact_id, SUM(cs_ext_sales_price) AS total_sales FROM mariadb.db1.catalog_sales, mysql.db1.date_dim, postgres.public.customer_address, postgres.public.item WHERE i_manufact_id IN (SELECT i_manufact_id FROM mysql.db1.item WHERE i_category IN ('Books')) AND cs_item_sk = i_item_sk AND cs_sold_date_sk = d_date_sk AND d_year = 1999 AND d_moy = 3 AND cs_bill_addr_sk = ca_address_sk AND ca_gmt_offset = -5 GROUP BY i_manufact_id), ws AS (SELECT i_manufact_id, SUM(ws_ext_sales_price) AS total_sales FROM mariadb.db1.web_sales, mysql.db1.date_dim, mysql.db1.customer_address, mariadb.db1.item WHERE i_manufact_id IN (SELECT i_manufact_id FROM mariadb.db1.item WHERE i_category IN ('Books')) AND ws_item_sk = i_item_sk AND ws_sold_date_sk = d_date_sk AND d_year = 1999 AND d_moy = 3 AND ws_bill_addr_sk = ca_address_sk AND ca_gmt_offset = -5 GROUP BY i_manufact_id) SELECT i_manufact_id, SUM(total_sales) AS total_sales FROM (SELECT * FROM ss UNION ALL SELECT * FROM cs UNION ALL SELECT * FROM ws) AS tmp1 GROUP BY i_manufact_id ORDER BY total_sales NULLS FIRST LIMIT 100