SELECT ca_state, cd_gender, cd_marital_status, cd_dep_count, COUNT(*) AS cnt1, AVG(cd_dep_count), MAX(cd_dep_count), SUM(cd_dep_count), cd_dep_employed_count, COUNT(*) AS cnt2, AVG(cd_dep_employed_count), MAX(cd_dep_employed_count), SUM(cd_dep_employed_count), cd_dep_college_count, COUNT(*) AS cnt3, AVG(cd_dep_college_count), MAX(cd_dep_college_count), SUM(cd_dep_college_count) FROM postgres.public.customer AS c, postgres.public.customer_address AS ca, mysql.db1.customer_demographics WHERE c.c_current_addr_sk = ca.ca_address_sk AND cd_demo_sk = c.c_current_cdemo_sk AND EXISTS(SELECT * FROM mysql.db1.store_sales, postgres.public.date_dim WHERE c.c_customer_sk = ss_customer_sk AND ss_sold_date_sk = d_date_sk AND d_year = 1999 AND d_qoy < 4) AND (EXISTS(SELECT * FROM postgres.public.web_sales, postgres.public.date_dim WHERE c.c_customer_sk = ws_bill_customer_sk AND ws_sold_date_sk = d_date_sk AND d_year = 1999 AND d_qoy < 4) OR EXISTS(SELECT * FROM mariadb.db1.catalog_sales, mariadb.db1.date_dim WHERE c.c_customer_sk = cs_ship_customer_sk AND cs_sold_date_sk = d_date_sk AND d_year = 1999 AND d_qoy < 4)) GROUP BY ca_state, cd_gender, cd_marital_status, cd_dep_count, cd_dep_employed_count, cd_dep_college_count ORDER BY ca_state NULLS FIRST, cd_gender NULLS FIRST, cd_marital_status NULLS FIRST, cd_dep_count NULLS FIRST, cd_dep_employed_count NULLS FIRST, cd_dep_college_count NULLS FIRST LIMIT 100