WITH ss AS (SELECT i_item_id, SUM(ss_ext_sales_price) AS total_sales FROM postgres.public.store_sales, mysql.db1.date_dim, mysql.db1.customer_address, mysql.db1.item WHERE i_item_id IN (SELECT i_item_id FROM mysql.db1.item WHERE i_color IN ('orchid', 'chiffon', 'lace')) AND ss_item_sk = i_item_sk AND ss_sold_date_sk = d_date_sk AND d_year = 2000 AND d_moy = 1 AND ss_addr_sk = ca_address_sk AND ca_gmt_offset = -8 GROUP BY i_item_id), cs AS (SELECT i_item_id, SUM(cs_ext_sales_price) AS total_sales FROM mariadb.db1.catalog_sales, mysql.db1.date_dim, postgres.public.customer_address, mysql.db1.item WHERE i_item_id IN (SELECT i_item_id FROM mariadb.db1.item WHERE i_color IN ('orchid', 'chiffon', 'lace')) AND cs_item_sk = i_item_sk AND cs_sold_date_sk = d_date_sk AND d_year = 2000 AND d_moy = 1 AND cs_bill_addr_sk = ca_address_sk AND ca_gmt_offset = -8 GROUP BY i_item_id), ws AS (SELECT i_item_id, SUM(ws_ext_sales_price) AS total_sales FROM postgres.public.web_sales, mariadb.db1.date_dim, mysql.db1.customer_address, mariadb.db1.item WHERE i_item_id IN (SELECT i_item_id FROM mariadb.db1.item WHERE i_color IN ('orchid', 'chiffon', 'lace')) AND ws_item_sk = i_item_sk AND ws_sold_date_sk = d_date_sk AND d_year = 2000 AND d_moy = 1 AND ws_bill_addr_sk = ca_address_sk AND ca_gmt_offset = -8 GROUP BY i_item_id) SELECT i_item_id, SUM(total_sales) AS total_sales FROM (SELECT * FROM ss UNION ALL SELECT * FROM cs UNION ALL SELECT * FROM ws) AS tmp1 GROUP BY i_item_id ORDER BY total_sales NULLS FIRST, i_item_id NULLS FIRST LIMIT 100