SELECT CAST(amc AS DECIMAL(15, 4)) / CAST(pmc AS DECIMAL(15, 4)) AS am_pm_ratio FROM (SELECT COUNT(*) AS amc FROM mariadb.db1.web_sales, mysql.db1.household_demographics, postgres.public.time_dim, mariadb.db1.web_page WHERE ws_sold_time_sk = time_dim.t_time_sk AND ws_ship_hdemo_sk = household_demographics.hd_demo_sk AND ws_web_page_sk = web_page.wp_web_page_sk AND time_dim.t_hour BETWEEN 6 AND 6 + 1 AND household_demographics.hd_dep_count = 8 AND web_page.wp_char_count BETWEEN 5000 AND 5200) AS at, (SELECT COUNT(*) AS pmc FROM mysql.db1.web_sales, postgres.public.household_demographics, mariadb.db1.time_dim, mariadb.db1.web_page WHERE ws_sold_time_sk = time_dim.t_time_sk AND ws_ship_hdemo_sk = household_demographics.hd_demo_sk AND ws_web_page_sk = web_page.wp_web_page_sk AND time_dim.t_hour BETWEEN 14 AND 14 + 1 AND household_demographics.hd_dep_count = 8 AND web_page.wp_char_count BETWEEN 5000 AND 5200) AS pt ORDER BY am_pm_ratio NULLS FIRST LIMIT 100