SELECT COUNT(*) FROM postgres.public.store_sales, mysql.db1.household_demographics, mariadb.db1.time_dim, mariadb.db1.store WHERE ss_sold_time_sk = time_dim.t_time_sk AND ss_hdemo_sk = household_demographics.hd_demo_sk AND ss_store_sk = s_store_sk AND time_dim.t_hour = 8 AND time_dim.t_minute >= 30 AND household_demographics.hd_dep_count = 5 AND store.s_store_name = 'ese' ORDER BY COUNT(*) NULLS FIRST LIMIT 100