SELECT i_item_id, i_item_desc, i_category, i_class, i_current_price, SUM(ws_ext_sales_price) AS itemrevenue, CAST(SUM(ws_ext_sales_price) * 100 AS DOUBLE) / SUM(SUM(ws_ext_sales_price)) OVER (PARTITION BY i_class) AS revenueratio FROM postgres2.public.web_sales, postgres1.public.item, postgres1.public.date_dim WHERE ws_item_sk = i_item_sk AND i_category IN ('Jewelry', 'Sports', 'Books') AND ws_sold_date_sk = d_date_sk AND d_date BETWEEN CAST('2001-01-12' AS DATE) AND (CAST('2001-01-12' AS DATE) + 30 AS days) GROUP BY i_item_id, i_item_desc, i_category, i_class, i_current_price ORDER BY i_category NULLS FIRST, i_class NULLS FIRST, i_item_id NULLS FIRST, i_item_desc NULLS FIRST, revenueratio NULLS FIRST LIMIT 100