SELECT i_item_id, i_item_desc, i_current_price FROM postgres1.public.item, postgres3.public.inventory, postgres2.public.date_dim, postgres3.public.catalog_sales WHERE i_current_price BETWEEN 22 AND 22 + 30 AND inv_item_sk = i_item_sk AND d_date_sk = inv_date_sk AND d_date BETWEEN CAST('2001-06-02' AS DATE) AND (CAST('2001-06-02' AS DATE) + 60 AS days) AND i_manufact_id IN (678, 964, 918, 849) AND inv_quantity_on_hand BETWEEN 100 AND 500 AND cs_item_sk = i_item_sk GROUP BY i_item_id, i_item_desc, i_current_price ORDER BY i_item_id NULLS FIRST LIMIT 100