SELECT COUNT(*) FROM (SELECT DISTINCT c_last_name, c_first_name, d_date FROM postgres1.public.store_sales, postgres2.public.date_dim, postgres1.public.customer WHERE store_sales.ss_sold_date_sk = date_dim.d_date_sk AND store_sales.ss_customer_sk = customer.c_customer_sk AND d_month_seq BETWEEN 1212 AND 1212 + 11 INTERSECT SELECT DISTINCT c_last_name, c_first_name, d_date FROM postgres3.public.catalog_sales, postgres2.public.date_dim, postgres3.public.customer WHERE catalog_sales.cs_sold_date_sk = date_dim.d_date_sk AND catalog_sales.cs_bill_customer_sk = customer.c_customer_sk AND d_month_seq BETWEEN 1212 AND 1212 + 11 INTERSECT SELECT DISTINCT c_last_name, c_first_name, d_date FROM postgres3.public.web_sales, postgres2.public.date_dim, postgres3.public.customer WHERE web_sales.ws_sold_date_sk = date_dim.d_date_sk AND web_sales.ws_bill_customer_sk = customer.c_customer_sk AND d_month_seq BETWEEN 1212 AND 1212 + 11) AS hot_cust LIMIT 100