WITH ss AS (SELECT i_item_id, SUM(ss_ext_sales_price) AS total_sales FROM postgres3.public.store_sales, postgres1.public.date_dim, postgres1.public.customer_address, postgres1.public.item WHERE i_item_id IN (SELECT i_item_id FROM postgres3.public.item WHERE i_color IN ('orchid', 'chiffon', 'lace')) AND ss_item_sk = i_item_sk AND ss_sold_date_sk = d_date_sk AND d_year = 2000 AND d_moy = 1 AND ss_addr_sk = ca_address_sk AND ca_gmt_offset = -8 GROUP BY i_item_id), cs AS (SELECT i_item_id, SUM(cs_ext_sales_price) AS total_sales FROM postgres2.public.catalog_sales, postgres1.public.date_dim, postgres3.public.customer_address, postgres3.public.item WHERE i_item_id IN (SELECT i_item_id FROM postgres2.public.item WHERE i_color IN ('orchid', 'chiffon', 'lace')) AND cs_item_sk = i_item_sk AND cs_sold_date_sk = d_date_sk AND d_year = 2000 AND d_moy = 1 AND cs_bill_addr_sk = ca_address_sk AND ca_gmt_offset = -8 GROUP BY i_item_id), ws AS (SELECT i_item_id, SUM(ws_ext_sales_price) AS total_sales FROM postgres3.public.web_sales, postgres1.public.date_dim, postgres2.public.customer_address, postgres3.public.item WHERE i_item_id IN (SELECT i_item_id FROM postgres1.public.item WHERE i_color IN ('orchid', 'chiffon', 'lace')) AND ws_item_sk = i_item_sk AND ws_sold_date_sk = d_date_sk AND d_year = 2000 AND d_moy = 1 AND ws_bill_addr_sk = ca_address_sk AND ca_gmt_offset = -8 GROUP BY i_item_id) SELECT i_item_id, SUM(total_sales) AS total_sales FROM (SELECT * FROM ss UNION ALL SELECT * FROM cs UNION ALL SELECT * FROM ws) AS tmp1 GROUP BY i_item_id ORDER BY total_sales NULLS FIRST, i_item_id NULLS FIRST LIMIT 100