SELECT a.ca_state AS state, COUNT(*) AS cnt FROM postgres3.public.customer_address AS a, postgres3.public.customer AS c, postgres1.public.store_sales AS s, postgres2.public.date_dim AS d, postgres3.public.item AS i WHERE a.ca_address_sk = c.c_current_addr_sk AND c.c_customer_sk = s.ss_customer_sk AND s.ss_sold_date_sk = d.d_date_sk AND s.ss_item_sk = i.i_item_sk AND d.d_month_seq = (SELECT DISTINCT (d_month_seq) FROM postgres1.public.date_dim WHERE d_year = 2000 AND d_moy = 2) AND i.i_current_price > 1.2 * (SELECT AVG(j.i_current_price) FROM postgres1.public.item AS j WHERE j.i_category = i.i_category) GROUP BY a.ca_state HAVING COUNT(*) >= 10 ORDER BY cnt NULLS FIRST, a.ca_state NULLS FIRST LIMIT 100