WITH ssr AS (SELECT s_store_id AS store_id, SUM(ss_ext_sales_price) AS sales, SUM(COALESCE(sr_return_amt, 0)) AS returns, SUM(ss_net_profit - COALESCE(sr_net_loss, 0)) AS profit FROM postgres3.public.store_sales LEFT OUTER JOIN postgres1.public.store_returns ON (ss_item_sk = sr_item_sk AND ss_ticket_number = sr_ticket_number), postgres1.public.date_dim, postgres1.public.store, postgres2.public.item, postgres1.public.promotion WHERE ss_sold_date_sk = d_date_sk AND d_date BETWEEN CAST('1998-08-04' AS DATE) AND (CAST('1998-08-04' AS DATE) + 30 AS days) AND ss_store_sk = s_store_sk AND ss_item_sk = i_item_sk AND i_current_price > 50 AND ss_promo_sk = p_promo_sk AND p_channel_tv = 'N' GROUP BY s_store_id), csr AS (SELECT cp_catalog_page_id AS catalog_page_id, SUM(cs_ext_sales_price) AS sales, SUM(COALESCE(cr_return_amount, 0)) AS returns, SUM(cs_net_profit - COALESCE(cr_net_loss, 0)) AS profit FROM postgres3.public.catalog_sales LEFT OUTER JOIN postgres1.public.catalog_returns ON (cs_item_sk = cr_item_sk AND cs_order_number = cr_order_number), postgres2.public.date_dim, postgres1.public.catalog_page, postgres3.public.item, postgres1.public.promotion WHERE cs_sold_date_sk = d_date_sk AND d_date BETWEEN CAST('1998-08-04' AS DATE) AND (CAST('1998-08-04' AS DATE) + 30 AS days) AND cs_catalog_page_sk = cp_catalog_page_sk AND cs_item_sk = i_item_sk AND i_current_price > 50 AND cs_promo_sk = p_promo_sk AND p_channel_tv = 'N' GROUP BY cp_catalog_page_id), wsr AS (SELECT web_site_id, SUM(ws_ext_sales_price) AS sales, SUM(COALESCE(wr_return_amt, 0)) AS returns, SUM(ws_net_profit - COALESCE(wr_net_loss, 0)) AS profit FROM postgres3.public.web_sales LEFT OUTER JOIN postgres2.public.web_returns ON (ws_item_sk = wr_item_sk AND ws_order_number = wr_order_number), postgres1.public.date_dim, postgres1.public.web_site, postgres3.public.item, postgres1.public.promotion WHERE ws_sold_date_sk = d_date_sk AND d_date BETWEEN CAST('1998-08-04' AS DATE) AND (CAST('1998-08-04' AS DATE) + 30 AS days) AND ws_web_site_sk = web_site_sk AND ws_item_sk = i_item_sk AND i_current_price > 50 AND ws_promo_sk = p_promo_sk AND p_channel_tv = 'N' GROUP BY web_site_id) SELECT channel, id, SUM(sales) AS sales, SUM(returns) AS returns, SUM(profit) AS profit FROM (SELECT 'store channel' AS channel, CONCAT(CAST('store' AS VARCHAR), CAST(store_id AS VARCHAR)) AS id, sales, returns, profit FROM ssr UNION ALL SELECT 'catalog channel' AS channel, CONCAT(CAST('catalog_page' AS VARCHAR), CAST(catalog_page_id AS VARCHAR)) AS id, sales, returns, profit FROM csr UNION ALL SELECT 'web channel' AS channel, CONCAT(CAST('web_site' AS VARCHAR), CAST(web_site_id AS VARCHAR)) AS id, sales, returns, profit FROM wsr) AS x GROUP BY ROLLUP (channel, id) ORDER BY channel NULLS FIRST, id NULLS FIRST LIMIT 100