SELECT c_customer_id AS customer_id, CONCAT(CAST(COALESCE(c_last_name, '') AS VARCHAR), CAST(', ' AS VARCHAR), CAST(COALESCE(c_first_name, '') AS VARCHAR)) AS customername FROM postgres2.public.customer, postgres3.public.customer_address, postgres3.public.customer_demographics, postgres3.public.household_demographics, postgres2.public.income_band, postgres2.public.store_returns WHERE ca_city = 'Hopewell' AND c_current_addr_sk = ca_address_sk AND ib_lower_bound >= 32287 AND ib_upper_bound <= 32287 + 50000 AND ib_income_band_sk = hd_income_band_sk AND cd_demo_sk = c_current_cdemo_sk AND hd_demo_sk = c_current_hdemo_sk AND sr_cdemo_sk = cd_demo_sk ORDER BY c_customer_id NULLS FIRST LIMIT 100