SELECT SUM(ws_ext_discount_amt) AS "Excess Discount Amount" FROM postgres1.public.web_sales, postgres1.public.item, postgres2.public.date_dim WHERE i_manufact_id = 269 AND i_item_sk = ws_item_sk AND d_date BETWEEN '1998-03-18' AND (CAST('1998-03-18' AS DATE) + 90 AS days) AND d_date_sk = ws_sold_date_sk AND ws_ext_discount_amt > (SELECT 1.3 * AVG(ws_ext_discount_amt) FROM postgres2.public.web_sales, postgres1.public.date_dim WHERE ws_item_sk = i_item_sk AND d_date BETWEEN '1998-03-18' AND (CAST('1998-03-18' AS DATE) + 90 AS days) AND d_date_sk = ws_sold_date_sk) ORDER BY SUM(ws_ext_discount_amt) NULLS FIRST LIMIT 100