SELECT COUNT(*) FROM postgres1.public.store_sales, postgres1.public.household_demographics, postgres2.public.time_dim, postgres3.public.store WHERE ss_sold_time_sk = time_dim.t_time_sk AND ss_hdemo_sk = household_demographics.hd_demo_sk AND ss_store_sk = s_store_sk AND time_dim.t_hour = 8 AND time_dim.t_minute >= 30 AND household_demographics.hd_dep_count = 5 AND store.s_store_name = 'ese' ORDER BY COUNT(*) NULLS FIRST LIMIT 100