SELECT w_state, i_item_id, SUM(CASE WHEN (CAST(d_date AS DATE) < CAST('1998-04-08' AS DATE)) THEN cs_sales_price - COALESCE(cr_refunded_cash, 0) ELSE 0 END) AS sales_before, SUM(CASE WHEN (CAST(d_date AS DATE) >= CAST('1998-04-08' AS DATE)) THEN cs_sales_price - COALESCE(cr_refunded_cash, 0) ELSE 0 END) AS sales_after FROM postgres1.public.catalog_sales LEFT OUTER JOIN postgres1.public.catalog_returns ON (cs_order_number = cr_order_number AND cs_item_sk = cr_item_sk), postgres1.public.warehouse, postgres2.public.item, postgres1.public.date_dim WHERE i_current_price BETWEEN 0.99 AND 1.49 AND i_item_sk = cs_item_sk AND cs_warehouse_sk = w_warehouse_sk AND cs_sold_date_sk = d_date_sk AND d_date BETWEEN (CAST('1998-04-08' AS DATE) - INTERVAL '30' DAY) AND (CAST('1998-04-08' AS DATE) + INTERVAL '30' DAY) GROUP BY w_state, i_item_id ORDER BY w_state NULLS FIRST, i_item_id NULLS FIRST LIMIT 100