SELECT cd_gender, cd_marital_status, cd_education_status, COUNT(*) AS cnt1, cd_purchase_estimate, COUNT(*) AS cnt2, cd_credit_rating, COUNT(*) AS cnt3 FROM postgres2.public.customer AS c, postgres2.public.customer_address AS ca, postgres3.public.customer_demographics WHERE c.c_current_addr_sk = ca.ca_address_sk AND ca_state IN ('CO', 'IL', 'MN') AND cd_demo_sk = c.c_current_cdemo_sk AND EXISTS(SELECT * FROM postgres3.public.store_sales, postgres2.public.date_dim WHERE c.c_customer_sk = ss_customer_sk AND ss_sold_date_sk = d_date_sk AND d_year = 1999 AND d_moy BETWEEN 1 AND 1 + 2) AND (NOT EXISTS(SELECT * FROM postgres2.public.web_sales, postgres3.public.date_dim WHERE c.c_customer_sk = ws_bill_customer_sk AND ws_sold_date_sk = d_date_sk AND d_year = 1999 AND d_moy BETWEEN 1 AND 1 + 2) AND NOT EXISTS(SELECT * FROM postgres2.public.catalog_sales, postgres3.public.date_dim WHERE c.c_customer_sk = cs_ship_customer_sk AND cs_sold_date_sk = d_date_sk AND d_year = 1999 AND d_moy BETWEEN 1 AND 1 + 2)) GROUP BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating ORDER BY cd_gender NULLS FIRST, cd_marital_status NULLS FIRST, cd_education_status NULLS FIRST, cd_purchase_estimate NULLS FIRST, cd_credit_rating NULLS FIRST LIMIT 100