SELECT SUM(cs_ext_discount_amt) AS "excess discount amount" FROM postgres2.public.catalog_sales, postgres3.public.item, postgres3.public.date_dim WHERE i_manufact_id = 269 AND i_item_sk = cs_item_sk AND d_date BETWEEN '1998-03-18' AND (CAST('1998-03-18' AS DATE) + INTERVAL '90' DAY) AND d_date_sk = cs_sold_date_sk AND cs_ext_discount_amt > (SELECT 1.3 * AVG(cs_ext_discount_amt) FROM postgres1.public.catalog_sales, postgres2.public.date_dim WHERE cs_item_sk = i_item_sk AND d_date BETWEEN '1998-03-18' AND (CAST('1998-03-18' AS DATE) + INTERVAL '90' DAY) AND d_date_sk = cs_sold_date_sk) LIMIT 100